  // Inventory Management
  app.post("/api/inventory/update-stock", async (req: TenantRequest, res) => {
    try {
      const { productId, quantity, type, notes, trackInventory } = req.body;
      const tenantDb = await getTenantDatabase(req);

      // Get current product
      const [product] = await db
        .select()
        .from(products)
        .where(eq(products.id, productId))
        .limit(1);
      if (!product) {
        return res.status(404).json({ error: "Product not found" });
      }

      let newStock = product.stock;
      switch (type) {
        case "add":
          newStock += quantity;
          break;
        case "subtract":
          newStock = Math.max(0, product.stock - quantity);
          break;
        case "set":
          newStock = quantity;
          break;
      }

      // Update product stock and trackInventory
      const updateData: any = { stock: newStock };
      if (trackInventory !== undefined) {
        updateData.trackInventory = trackInventory;
      }

      await db
        .update(products)
        .set(updateData)
        .where(eq(products.id, productId));

      // Create inventory transaction record
      await db.insert(inventoryTransactions).values({
        productId,
        type,
        quantity,
        previousStock: product.stock,
        newStock,
        notes: notes || null,
        createdAt: new Date().toISOString(),
      });

      res.json({ success: true, newStock });
    } catch (error) {
      console.error("Stock update error:", error);
      res.status(500).json({ error: "Failed to update stock" });
    }
  });

