import { Pool } from "pg";
import { drizzle } from "drizzle-orm/node-postgres";
import * as schema from "@shared/schema";

interface TenantConfig {
  subdomain: string;
  databaseUrl: string;
  storeName: string;
  isActive: boolean;
}

class TenantManager {
  private tenants: Map<string, TenantConfig> = new Map();
  private dbConnections: Map<string, any> = new Map();

  constructor() {
    this.loadTenants();
  }

  private loadTenants() {
    // Load tenant configurations from environment or main database
    const tenantsConfig = [
      {
        subdomain: "demo",
        databaseUrl: process.env.EXTERNAL_DB_URL || process.env.DATABASE_URL!,
        storeName: "Demo Store - C·ª≠a h√†ng demo",
        isActive: true,
      },
      {
        subdomain: "hazkitchen",
        databaseUrl: process.env.EXTERNAL_DB_URL || process.env.DATABASE_URL!,
        storeName: "Store 1 - C·ª≠a h√†ng s·ªë 1",
        isActive: true,
      },
      // Add more tenants as needed
    ];

    tenantsConfig.forEach((config) => {
      this.tenants.set(config.subdomain, config);
    });
  }

  getTenantBySubdomain(subdomain: string): TenantConfig | null {
    return this.tenants.get(subdomain) || null;
  }

  async getDatabaseConnection(subdomain: string) {
    if (this.dbConnections.has(subdomain)) {
      return this.dbConnections.get(subdomain);
    }

    const tenant = this.getTenantBySubdomain(subdomain);
    if (!tenant) {
      throw new Error(`Tenant not found: ${subdomain}`);
    }

    const pool = new Pool({
      connectionString: tenant.databaseUrl,
      max: 10,
      idleTimeoutMillis: 60000,
      connectionTimeoutMillis: 10000,
      acquireTimeoutMillis: 10000,
      ssl: tenant.databaseUrl?.includes("1.55.212.135")
        ? false // Disable SSL for external server
        : tenant.databaseUrl?.includes("neon")
          ? { rejectUnauthorized: false }
          : undefined,
    });

    const db = drizzle({ client: pool, schema });
    this.dbConnections.set(subdomain, db);

    return db;
  }

  getAllTenants(): TenantConfig[] {
    return Array.from(this.tenants.values());
  }

  addTenant(config: TenantConfig) {
    this.tenants.set(config.subdomain, config);
  }

  removeTenant(subdomain: string) {
    this.tenants.delete(subdomain);
    this.dbConnections.delete(subdomain);
  }
}

export const tenantManager = new TenantManager();

server/tenant-middleware.ts.content:
import { Request, Response, NextFunction } from 'express';
import { tenantManager } from './tenant-manager';

export interface TenantRequest extends Request {
  tenant?: {
    subdomain: string;
    config: any;
    db: any;
  };
}

export function tenantMiddleware(req: TenantRequest, res: Response, next: NextFunction) {
  try {
    // For development, allow tenant to be specified via query parameter
    let subdomain = req.query.tenant as string;

    // If no tenant parameter, extract from host header
    if (!subdomain) {
      const host = req.get('host') || req.get('x-forwarded-host') || '';
      subdomain = extractSubdomain(host);
    }

    if (!subdomain) {
      return res.status(400).json({ 
        error: 'Invalid subdomain',
        message: 'Please access through a valid subdomain (e.g., store1.yourapp.replit.app) or add ?tenant=demo parameter'
      });
    }

    const tenantConfig = tenantManager.getTenantBySubdomain(subdomain);
    if (!tenantConfig) {
      return res.status(404).json({ 
        error: 'Tenant not found',
        message: `Store '${subdomain}' not found`
      });
    }

    if (!tenantConfig.isActive) {
      return res.status(403).json({ 
        error: 'Store inactive',
        message: 'This store is currently inactive'
      });
    }

    // Attach tenant info to request
    req.tenant = {
      subdomain,
      config: tenantConfig,
      db: null // Will be loaded lazily
    };

    next();
  } catch (error) {
    console.error('Tenant middleware error:', error);
    res.status(500).json({ error: 'Internal server error' });
  }
}

function extractSubdomain(host: string): string | null {
  // Remove port if present
  const hostWithoutPort = host.split(':')[0];

  // Handle development environment (Replit)
  if (hostWithoutPort.includes('replit.dev') || hostWithoutPort.includes('replit.co')) {
    // Format: c2bc841a-a304-4215-98b0-ab6349baf209-00-21m0gfgjl6ikj.worf.replit.dev
    // For replit dev URLs, we'll look for URL parameters or use a default
    return 'demo'; // Default for development
  }

  // Handle production custom domain with subdomain
  if (hostWithoutPort.includes('replit.app')) {
    // Format: subdomain.appname.replit.app
    const parts = hostWithoutPort.split('.');
    if (parts.length >= 3) {
      return parts[0];
    }
  }

  // Handle custom domain with subdomain
  const parts = hostWithoutPort.split('.');
  if (parts.length >= 2) {
    const subdomain = parts[0];
    // Skip www prefix
    if (subdomain === 'www') {
      return 'demo'; // Default when www is used
    }
    return subdomain;
  }

  return 'demo'; // Default subdomain for development
}

export async function getTenantDatabase(req: TenantRequest) {
  if (!req.tenant) {
    throw new Error('Tenant not found in request');
  }

  if (!req.tenant.db) {
    req.tenant.db = await tenantManager.getDatabaseConnection(req.tenant.subdomain);
  }

  return req.tenant.db;
}

