        title: t("inventory.updateSuccess") || "C·∫≠p nh·∫≠t th√†nh c√¥ng",
        description:
          t("inventory.updateSuccessDescription") ||
          "Th√¥ng tin s·∫£n ph·∫©m ƒë√£ ƒë∆∞·ª£c c·∫≠p nh·∫≠t",
      });
