        title: t("inventory.createSuccess") || "T·∫°o m·ªõi th√†nh c√¥ng",
        description:
          t("inventory.createSuccessDescription") ||
          "S·∫£n ph·∫©m m·ªõi ƒë√£ ƒë∆∞·ª£c th√™m v√†o kho h√†ng",
      });
    },
    onError: (error: any) => {
      console.error("Create product error:", error);

      // Check if it's a duplicate SKU error
      if (
        error?.response?.status === 409 &&
        error?.response?.data?.code === "DUPLICATE_SKU"
      ) {
        toast({
          title: t("inventory.duplicateSku") || "ƒê√£ t·ªìn t·∫°i s·∫£n ph·∫©m trong kho",
          description:
            t("inventory.duplicateSkuDescription") ||
            "SKU n√†y ƒë√£ ƒë∆∞·ª£c s·ª≠ d·ª•ng cho s·∫£n ph·∫©m kh√°c",
          variant: "destructive",
        });
      } else {
        toast({
          title: t("inventory.createFailed") || "T·∫°o m·ªõi th·∫•t b·∫°i",
          description:
            t("inventory.createFailedDescription") ||
            "Kh√¥ng th·ªÉ t·∫°o s·∫£n ph·∫©m m·ªõi. Vui l√≤ng th·ª≠ l·∫°i.",
          variant: "destructive",
        });
      }
