    onError: (error) => {
      console.error("Delete product error:", error);

      let errorMessage = t("inventory.deleteFailedDescription") || "Kh√¥ng th·ªÉ x√≥a s·∫£n ph·∫©m. Vui l√≤ng th·ª≠ l·∫°i.";

      if (error instanceof Error && error.message.includes("Cannot delete product")) {
        if (error.message.includes("transactions")) {
          errorMessage = "Kh√¥ng th·ªÉ x√≥a s·∫£n ph·∫©m v√¨ ƒë√£ ƒë∆∞·ª£c s·ª≠ d·ª•ng trong c√°c giao d·ªãch b√°n h√†ng.";
