      });
    },
    onError: (error) => {
      console.error("Cleanup error:", error);
      toast({
        title: "D·ªçn d·∫πp th·∫•t b·∫°i",
        description: "Kh√¥ng th·ªÉ x√≥a c√°c s·∫£n ph·∫©m v√¥ hi·ªáu. Vui l√≤ng th·ª≠ l·∫°i.",
        variant: "destructive",
      });
    },
  });



  const filteredProducts = products.filter((product) => {
    const matchesSearch =
      product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      product.sku.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesCategory =
      selectedCategory === "all" ||
      product.categoryId.toString() === selectedCategory;
    const matchesStock =
      stockFilter === "all" ||
      (stockFilter === "low" && product.stock <= 10 && product.stock > 0) ||
      (stockFilter === "out" && product.stock === 0) ||
      (stockFilter === "in" && product.stock > 10);

    return matchesSearch && matchesCategory && matchesStock;
  });

  const lowStockCount = products.filter(
    (p) => p.stock <= 10 && p.stock > 0,
  ).length;
  const outOfStockCount = products.filter((p) => p.stock === 0).length;
  const totalValue = products.reduce(
    (sum, p) => sum + parseFloat(p.price) * p.stock,
    0,
  );

  const handleStockUpdate = (product: Product) => {
    setSelectedProduct(product);
    if (product.id === 0) {
      // Reset form for new product
      stockUpdateForm.reset({
        productId: 0,
        quantity: 0,
        type: "set",
        name: "",
        sku: "",
        price: "0",
        categoryId: categories[0]?.id || 1,
        productType: 1,
        trackInventory: true,
        taxRate: "8.00",
      });
    } else {
      // Load existing product data for editing
      stockUpdateForm.reset({
        productId: product.id,
        quantity: 1,
        type: "add",
        name: product.name,
        sku: product.sku,
        price: product.price,
        categoryId: product.categoryId,
        productType: product.productType || 1,
        trackInventory: product.trackInventory !== false,
      });
    }
    setShowStockDialog(true);
  };

  const handleDeleteProduct = (product: Product) => {
    if (
      window.confirm(
        `B·∫°n c√≥ ch·∫Øc ch·∫Øn mu·ªën x√≥a vƒ©nh vi·ªÖn s·∫£n ph·∫©m "${product.name}" kh·ªèi c∆° s·ªü d·ªØ li·ªáu? H√†nh ƒë·ªông n√†y kh√¥ng th·ªÉ ho√†n t√°c.`,
      )
    ) {
      deleteProductMutation.mutate(product.id);
    }
  };

  const handleCleanupInactiveProducts = () => {
    if (
      window.confirm(
        "B·∫°n c√≥ ch·∫Øc ch·∫Øn mu·ªën x√≥a vƒ©nh vi·ªÖn t·∫•t c·∫£ s·∫£n ph·∫©m v√¥ hi·ªáu kh·ªèi c∆° s·ªü d·ªØ li·ªáu? H√†nh ƒë·ªông n√†y kh√¥ng th·ªÉ ho√†n t√°c.",
      )
    ) {
      cleanupMutation.mutate();
    }
  };

  const onStockUpdate = (data: StockUpdateForm) => {
    if (selectedProduct?.id === 0) {
      // Creating new product
      const newProductData = {
        name: data.name || "",
        sku: data.sku || "",
        price: data.price || "0",
        stock: data.quantity || 0,
        categoryId: data.categoryId || 1,
        productType: data.productType || 1,
        taxRate: data.taxRate || "8.00",
        imageUrl: null,
        isActive: true,
        trackInventory: data.trackInventory !== false,
      };
      console.log("Creating product with data:", newProductData);
      createProductMutation.mutate(newProductData);
    } else {
      // Updating existing product stock - include trackInventory
      const updateData = {
        productId: selectedProduct.id,
        quantity: data.quantity,
        type: data.type,
        notes: data.notes,
        trackInventory: data.trackInventory
      };
      console.log("Updating stock with data:", updateData);
      updateStockMutation.mutate(updateData);
    }
  };

  const getStockStatus = (stock: number) => {
    if (stock === 0)
      return { label: t("inventory.outOfStock"), color: "bg-red-500" };
    if (stock <= 10)
      return { label: t("inventory.lowStock"), color: "bg-yellow-500" };
    return { label: t("inventory.inStock"), color: "bg-green-500" };
  };

  return (
    <div className="min-h-screen bg-green-50 grocery-bg">
      <POSHeader />
      <RightSidebar />

      <div className="main-content pt-16 px-6">
        <div className="max-w-7xl mx-auto py-8">
          {/* Page Header */}
          <div className="flex justify-between items-center mb-8">
            <div>
              <h1 className="text-3xl font-bold text-gray-900">
                {t("inventory.title")}
              </h1>
              <p className="text-gray-600 mt-2">{t("inventory.description")}</p>
            </div>
          </div>

          {/* Statistics Cards */}
          <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
            <Card className="border-green-200">
              <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                <CardTitle className="text-sm font-medium text-gray-600">
                  {t("inventory.totalProducts")}
                </CardTitle>
                <Package className="h-4 w-4 text-green-600" />
              </CardHeader>
              <CardContent>
                <div className="text-2xl font-bold text-gray-900">
                  {products.length}
                </div>
              </CardContent>
            </Card>

            <Card className="border-green-200">
              <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                <CardTitle className="text-sm font-medium text-gray-600">
                  {t("inventory.lowStock")}
                </CardTitle>
                <AlertTriangle className="h-4 w-4 text-yellow-600" />
              </CardHeader>
              <CardContent>
                <div className="text-2xl font-bold text-yellow-600">
                  {lowStockCount}
                </div>
              </CardContent>
            </Card>

            <Card className="border-green-200">
              <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                <CardTitle className="text-sm font-medium text-gray-600">
                  {t("inventory.outOfStock")}
                </CardTitle>
                <AlertTriangle className="h-4 w-4 text-red-600" />
              </CardHeader>
              <CardContent>
                <div className="text-2xl font-bold text-red-600">
                  {outOfStockCount}
                </div>
              </CardContent>
            </Card>

            <Card className="border-green-200">
              <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                <CardTitle className="text-sm font-medium text-gray-600">
                  {t("inventory.totalValue")}
                </CardTitle>
                <TrendingUp className="h-4 w-4 text-green-600" />
              </CardHeader>
              <CardContent>
                <div className="text-2xl font-bold text-green-600">
