import { useState, useEffect } from "react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { useToast } from "@/hooks/use-toast";
import { Shield, Lock, Eye, EyeOff } from "lucide-react";
import { useQuery } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";

interface PinAuthProps {
  onAuthSuccess: () => void;
}

export function PinAuth({ onAuthSuccess }: PinAuthProps) {
  const [pin, setPin] = useState("");
  const [showPin, setShowPin] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const { toast } = useToast();

  // Fetch store settings ƒë·ªÉ l·∫•y PIN
  const { data: storeData } = useQuery({
    queryKey: ["/api/store-settings"],
    queryFn: async () => {
      const response = await apiRequest("GET", "/api/store-settings");
      return response.json();
    },
  });

  useEffect(() => {
    // Ki·ªÉm tra n·∫øu ƒë√£ ƒëƒÉng nh·∫≠p trong session n√†y
    const isAuthenticated = sessionStorage.getItem("pinAuthenticated");
    if (isAuthenticated === "true") {
      onAuthSuccess();
    }
  }, [onAuthSuccess]);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!pin.trim()) {
      toast({
        title: "L·ªói",
        description: "Vui l√≤ng nh·∫≠p m√£ PIN",
        variant: "destructive",
      });
      return;
    }

    setIsLoading(true);

    try {
      // Ki·ªÉm tra PIN v·ªõi d·ªØ li·ªáu t·ª´ store settings
      if (storeData?.pinCode && pin === storeData.pinCode) {
        // L∆∞u tr·∫°ng th√°i ƒëƒÉng nh·∫≠p v√†o sessionStorage
        sessionStorage.setItem("pinAuthenticated", "true");

        toast({
          title: "ƒêƒÉng nh·∫≠p th√†nh c√¥ng",
          description: "Ch√†o m·ª´ng b·∫°n ƒë·∫øn v·ªõi h·ªá th·ªëng POS",
        });

        onAuthSuccess();
      } else {
        toast({
          title: "M√£ PIN kh√¥ng ƒë√∫ng",
          description: "Vui l√≤ng ki·ªÉm tra l·∫°i m√£ PIN",
          variant: "destructive",
        });
        setPin("");
      }
    } catch (error) {
      toast({
        title: "L·ªói h·ªá th·ªëng",
        description: "C√≥ l·ªói x·∫£y ra khi x√°c th·ª±c",
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  const handlePinChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value.replace(/\D/g, ''); // Ch·ªâ cho ph√©p s·ªë
    if (value.length <= 6) {
      setPin(value);
    }
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter') {
      handleSubmit(e as any);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-green-400 via-green-500 to-green-600 flex items-center justify-center p-4">
      {/* Background Pattern */}
      <div className="absolute inset-0 overflow-hidden pointer-events-none">
        <div
          className="absolute inset-0 opacity-10"
          style={{
            backgroundImage: `radial-gradient(circle at 25% 25%, #ffffff 0%, transparent 50%),
                           radial-gradient(circle at 75% 25%, #ffffff 0%, transparent 50%),
                           radial-gradient(circle at 25% 75%, #ffffff 0%, transparent 50%),
                           radial-gradient(circle at 75% 75%, #ffffff 0%, transparent 50%)`,
            backgroundSize: "100px 100px",
          }}
        ></div>
      </div>

      <Card className="w-full max-w-md bg-white/95 backdrop-blur-sm shadow-2xl border-0 relative z-10">
        <CardHeader className="text-center space-y-4 pb-2">
          <div className="mx-auto w-16 h-16 bg-green-500 rounded-full flex items-center justify-center">
            <Shield className="w-8 h-8 text-white" />
          </div>
          <div>
            <CardTitle className="text-2xl font-bold text-gray-900">
              X√°c th·ª±c b·∫£o m·∫≠t
            </CardTitle>
            <CardDescription className="text-gray-600 mt-2">
              Nh·∫≠p m√£ PIN ƒë·ªÉ truy c·∫≠p h·ªá th·ªëng POS
            </CardDescription>
          </div>
        </CardHeader>

        <CardContent className="space-y-6 pt-4">
          <form onSubmit={handleSubmit} className="space-y-4">
            <div className="space-y-2">
              <Label htmlFor="pin" className="text-sm font-medium text-gray-700">
                M√£ PIN
              </Label>
              <div className="relative">
                <Input
                  id="pin"
                  type={showPin ? "text" : "password"}
                  value={pin}
                  onChange={handlePinChange}
                  onKeyPress={handleKeyPress}
                  placeholder="Nh·∫≠p m√£ PIN (4-6 ch·ªØ s·ªë)"
                  className="pr-10 text-center text-lg tracking-widest font-mono"
                  maxLength={6}
                  autoFocus
                  disabled={isLoading}
                />
                <Button
                  type="button"
                  variant="ghost"
                  size="sm"
                  className="absolute right-2 top-1/2 -translate-y-1/2 h-6 w-6 p-0"
                  onClick={() => setShowPin(!showPin)}
                >
                  {showPin ? (
                    <EyeOff className="w-4 h-4 text-gray-500" />
                  ) : (
                    <Eye className="w-4 h-4 text-gray-500" />
                  )}
                </Button>
              </div>
            </div>

            <Button
              type="submit"
              className="w-full bg-green-600 hover:bg-green-700 text-white py-3 text-lg font-semibold"
              disabled={isLoading || pin.length < 4}
            >
              {isLoading ? (
                <div className="flex items-center gap-2">
                  <div className="w-4 h-4 border-2 border-white/30 border-t-white rounded-full animate-spin"></div>
                  ƒêang x√°c th·ª±c...
                </div>
              ) : (
                <div className="flex items-center gap-2">
                  <Lock className="w-5 h-5" />
                  ƒêƒÉng nh·∫≠p
                </div>
              )}
            </Button>
          </form>

          <div className="text-center">
            <p className="text-xs text-gray-500">
              Li√™n h·ªá qu·∫£n tr·ªã vi√™n n·∫øu b·∫°n qu√™n m√£ PIN
            </p>
          </div>

          {/* Virtual Keypad for mobile */}
          <div className="grid grid-cols-3 gap-2 mt-6">
            {[1, 2, 3, 4, 5, 6, 7, 8, 9].map((num) => (
              <Button
                key={num}
                type="button"
                variant="outline"
                className="h-12 text-lg font-semibold"
                onClick={() => {
                  if (pin.length < 6) {
                    setPin(prev => prev + num);
                  }
                }}
                disabled={isLoading || pin.length >= 6}
              >
                {num}
              </Button>
            ))}
            <Button
              type="button"
              variant="outline"
              className="h-12 text-lg font-semibold text-red-600"
              onClick={() => setPin("")}
              disabled={isLoading || pin.length === 0}
            >
              X√≥a
            </Button>
            <Button
              type="button"
              variant="outline"
              className="h-12 text-lg font-semibold"
              onClick={() => {
                if (pin.length < 6) {
                  setPin(prev => prev + "0");
                }
              }}
              disabled={isLoading || pin.length >= 6}
            >
              0
            </Button>
            <Button
              type="button"
              variant="outline"
              className="h-12 text-lg font-semibold text-red-600"
              onClick={() => setPin(prev => prev.slice(0, -1))}
              disabled={isLoading || pin.length === 0}
            >
              ‚Üê
            </Button>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

