import { useState, useEffect } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogDescription } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { useTranslation } from "@/lib/i18n";
import type { Customer, InsertCustomer } from "@shared/schema";
import { z } from "zod";

const customerFormSchema = z.object({
  customerId: z.string().optional(),
  name: z.string().min(1, "Ïù¥Î¶ÑÏùÄ ÌïÑÏàòÏûÖÎãàÎã§"),
  phone: z.string().optional(),
  email: z.string().optional().refine((email) => !email || /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email), {
    message: "Ïò¨Î∞îÎ•∏ Ïù¥Î©îÏùº ÌòïÏãùÏù¥ ÏïÑÎãôÎãàÎã§"
  }),
  address: z.string().optional(),
  dateOfBirth: z.string().optional(),
  membershipLevel: z.enum(["Silver", "Gold", "VIP"]).optional(),
  notes: z.string().optional(),
  status: z.enum(["active", "inactive"]).optional(),
});

type CustomerFormData = z.infer<typeof customerFormSchema>;

interface CustomerFormModalProps {
  isOpen: boolean;
  onClose: () => void;
  customer?: Customer | null;
}

export function CustomerFormModal({ isOpen, onClose, customer }: CustomerFormModalProps) {
  const { toast } = useToast();
  const { t } = useTranslation();
  const queryClient = useQueryClient();

  // Generate customer ID for new customers
  const generateCustomerId = async () => {
    try {
      const response = await apiRequest("GET", "/api/customers/next-id");
      const data = await response.json();
      return data.nextId;
    } catch (error) {
      console.error("Error generating customer ID:", error);
      // Fallback to CUST001 if API fails
      return "CUST001";
    }
  };

  const form = useForm<CustomerFormData>({
    resolver: zodResolver(customerFormSchema),
    defaultValues: {
      customerId: customer?.customerId || "",
      name: customer?.name || "",
      phone: customer?.phone || "",
      email: customer?.email || "",
      address: customer?.address || "",
      dateOfBirth: customer?.dateOfBirth || "",
      membershipLevel: customer?.membershipLevel || "Silver",
      notes: customer?.notes || "",
      status: customer?.status || "active",
    },
  });

  // Reset form when customer changes and auto-generate ID for new customers
  useEffect(() => {
    if (customer) {
      form.reset({
        customerId: customer.customerId,
        name: customer.name,
        phone: customer.phone || "",
        email: customer.email || "",
        address: customer.address || "",
        dateOfBirth: customer.dateOfBirth || "",
        membershipLevel: customer.membershipLevel || "Silver",
        notes: customer.notes || "",
        status: customer.status || "active",
      });
    } else {
      // Auto-generate customer ID for new customers
      generateCustomerId().then((nextId) => {
        form.reset({
          customerId: nextId,
          name: "",
          phone: "",
          email: "",
          address: "",
          dateOfBirth: "",
          membershipLevel: "Silver",
          notes: "",
          status: "active",
        });
      });
    }
  }, [customer, form]);

  const createMutation = useMutation({
    mutationFn: async (data: CustomerFormData) => {
      const response = await apiRequest("POST", "/api/customers", data);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/customers"] });
      toast({
        title: t('common.success'),
        description: customer ? "Í≥†Í∞ù Ï†ïÎ≥¥Í∞Ä ÏóÖÎç∞Ïù¥Ìä∏ÎêòÏóàÏäµÎãàÎã§." : "ÏÉà Í≥†Í∞ùÏù¥ Ï∂îÍ∞ÄÎêòÏóàÏäµÎãàÎã§.",
      });
      onClose();
      form.reset();
    },
    onError: (error: any) => {
      toast({
        title: t('common.error'),
        description: error.message || "Í≥†Í∞ù Ï†ïÎ≥¥ Ï†ÄÏû•Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.",
        variant: "destructive",
      });
    },
  });

  const updateMutation = useMutation({
    mutationFn: async (data: CustomerFormData) => {
      const response = await apiRequest("PUT", `/api/customers/${customer!.id}`, data);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/customers"] });
      toast({
        title: t('common.success'),
        description: "Í≥†Í∞ù Ï†ïÎ≥¥Í∞Ä ÏóÖÎç∞Ïù¥Ìä∏ÎêòÏóàÏäµÎãàÎã§.",
      });
      onClose();
      form.reset();
    },
    onError: (error: any) => {
      toast({
        title: t('common.error'),
        description: error.message || "Í≥†Í∞ù Ï†ïÎ≥¥ ÏóÖÎç∞Ïù¥Ìä∏Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.",
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: CustomerFormData) => {
    // Clean up empty strings to null/undefined for optional fields
    const cleanData = {
      ...data,
      phone: data.phone || undefined,
      email: data.email || undefined,
      address: data.address || undefined,
      dateOfBirth: data.dateOfBirth || undefined,
      notes: data.notes || undefined,
      customerId: data.customerId || undefined,
    };

    if (customer) {
      updateMutation.mutate(cleanData);
    } else {
      createMutation.mutate(cleanData);
    }
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-2xl max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle>{customer ? t('customers.editCustomer') : t('customers.addCustomer')}</DialogTitle>
          <DialogDescription>
            {customer ? t('customers.customerFormDesc') : t('customers.customerFormDesc')}
          </DialogDescription>
        </DialogHeader>

        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
            <div className="grid grid-cols-2 gap-4">
              <FormField
                control={form.control}
                name="customerId"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>{t('customers.customerId')}</FormLabel>
                    <FormControl>
                      <Input 
                        {...field} 
                        placeholder={customer ? t('customers.customerId') : t('common.autoGenerated')} 
                        disabled={true}
                        className="bg-gray-50"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="name"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>{t('customers.name')} *</FormLabel>
                    <FormControl>
                      <Input {...field} placeholder={t('customers.namePlaceholder')} />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            <div className="grid grid-cols-2 gap-4">
              <FormField
                control={form.control}
                name="phone"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>{t('customers.phone')}</FormLabel>
                    <FormControl>
                      <Input {...field} placeholder={t('customers.phonePlaceholder')} />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="email"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>{t('customers.email')}</FormLabel>
                    <FormControl>
                      <Input {...field} type="email" placeholder={t('customers.emailPlaceholder')} />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            <FormField
              control={form.control}
              name="address"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>{t('customers.address')}</FormLabel>
                  <FormControl>
                    <Textarea {...field} placeholder={t('customers.addressPlaceholder')} rows={2} />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <div className="grid grid-cols-2 gap-4">
              <FormField
                control={form.control}
                name="dateOfBirth"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>{t('customers.birthday')}</FormLabel>
                    <FormControl>
                      <Input {...field} type="date" />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="membershipLevel"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>{t('customers.membershipLevel')}</FormLabel>
                    <Select onValueChange={field.onChange} value={field.value}>
                      <FormControl>
                        <SelectTrigger>
                          <SelectValue placeholder={t('common.select')} />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        <SelectItem value="Silver">Silver</SelectItem>
                        <SelectItem value="Gold">Gold</SelectItem>
                        <SelectItem value="VIP">VIP</SelectItem>
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            <FormField
              control={form.control}
              name="status"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>{t('customers.status')}</FormLabel>
                  <Select onValueChange={field.onChange} defaultValue={field.value}>
                    <FormControl>
                      <SelectTrigger>
                        <SelectValue placeholder={t('common.select')} />
                      </SelectTrigger>
                    </FormControl>
                    <SelectContent>
                      <SelectItem value="active">{t('common.active')}</SelectItem>
                      <SelectItem value="inactive">{t('common.inactive')}</SelectItem>
                    </SelectContent>
                  </Select>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="notes"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>{t('common.notes')}</FormLabel>
                  <FormControl>
                    <Textarea {...field} placeholder={t('common.notesPlaceholder')} rows={3} />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <div className="flex justify-end gap-2">
              <Button type="button" variant="outline" onClick={onClose}>
                {t('common.cancel')}
              </Button>
              <Button 
                type="submit" 
                disabled={createMutation.isPending || updateMutation.isPending}
              >
                {customer ? t('common.update') : t('common.create')}
              </Button>
            </div>
          </form>
        </Form>
      </DialogContent>
    </Dialog>
  );
}

import { useState } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
} from '@/components/ui/dialog';
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from '@/components/ui/form';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Textarea } from '@/components/ui/textarea';
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Separator } from '@/components/ui/separator';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { z } from 'zod';
import { useToast } from '@/hooks/use-toast';
import { apiRequest } from '@/lib/queryClient';
import { useTranslation } from '@/lib/i18n';
import { format } from 'date-fns';
import { CreditCard, TrendingUp, TrendingDown, Activity, Clock } from 'lucide-react';

const pointUpdateSchema = z.object({
  points: z.number().min(1, 'Points must be at least 1'),
  description: z.string().min(1, 'Description is required'),
  type: z.enum(['earned', 'redeemed', 'adjusted']),
});

type PointUpdateForm = z.infer<typeof pointUpdateSchema>;

interface CustomerPointsModalProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  customerId: number;
  customerName: string;
}

interface PointTransaction {
  id: number;
  customerId: number;
  type: 'earned' | 'redeemed' | 'adjusted' | 'expired';
  points: number;
  description: string;
  orderId?: number;
  employeeId?: number;
  previousBalance: number;
  newBalance: number;
  createdAt: string;
}

export function CustomerPointsModal({
  open,
  onOpenChange,
  customerId,
  customerName,
}: CustomerPointsModalProps) {
  const [activeTab, setActiveTab] = useState<'overview' | 'update' | 'history'>('overview');
  const queryClient = useQueryClient();
  const { toast } = useToast();
  const { t } = useTranslation();

  const form = useForm<PointUpdateForm>({
    resolver: zodResolver(pointUpdateSchema),
    defaultValues: {
      points: 0,
      description: '',
      type: 'earned',
    },
  });

  // Fetch customer points
  const { data: pointsData, isLoading: pointsLoading } = useQuery({
    queryKey: ['customer-points', customerId],
    queryFn: async () => {
      const response = await fetch(`/api/customers/${customerId}/points`);
      if (!response.ok) throw new Error('Failed to fetch customer points');
      return response.json();
    },
    enabled: open && !!customerId,
  });

  // Fetch point history
  const { data: pointHistory, isLoading: historyLoading } = useQuery({
    queryKey: ['customer-point-history', customerId],
    queryFn: async () => {
      const response = await fetch(`/api/customers/${customerId}/point-history`);
      if (!response.ok) throw new Error('Failed to fetch point history');
      return response.json();
    },
    enabled: open && !!customerId,
  });

  // Update points mutation
  const updatePointsMutation = useMutation({
    mutationFn: async (data: PointUpdateForm) => {
      const response = await fetch(`/api/customers/${customerId}/points`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(data),
      });
      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || 'Failed to update points');
      }
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({
        queryKey: ['customer-points', customerId],
      });
      queryClient.invalidateQueries({
        queryKey: ['customer-point-history', customerId],
      });
      queryClient.invalidateQueries({
        queryKey: ['/api/customers'],
      });
      toast({
        title: t("common.success"),
        description: t("customers.pointsUpdated"),
      });
      form.reset();
      setActiveTab('overview');
    },
    onError: (error: any) => {
      toast({
        title: t("customers.pointsUpdateFailed"),
        description: error.message || t("customers.pointsUpdateError"),
        variant: 'destructive',
      });
    },
  });

  const onSubmit = (data: PointUpdateForm) => {
    updatePointsMutation.mutate(data);
  };

  const getTransactionIcon = (type: string) => {
    switch (type) {
      case 'earned':
        return <TrendingUp className="h-4 w-4 text-green-600" />;
      case 'redeemed':
        return <TrendingDown className="h-4 w-4 text-red-600" />;
      case 'adjusted':
        return <Activity className="h-4 w-4 text-blue-600" />;
      default:
        return <Clock className="h-4 w-4 text-gray-600" />;
    }
  };

  const getTransactionColor = (type: string) => {
    switch (type) {
      case 'earned':
        return 'text-green-600';
      case 'redeemed':
        return 'text-red-600';
      case 'adjusted':
        return 'text-blue-600';
      default:
        return 'text-gray-600';
    }
  };

  const getTypeLabel = (type: string) => {
    switch (type) {
      case 'earned':
        return t("customers.earned");
      case 'redeemed':
        return t("customers.redeemed");
      case 'adjusted':
        return t("customers.adjusted");
      case 'expired':
        return t("customers.expired");
      default:
        return type;
    }
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <CreditCard className="h-5 w-5" />
            {customerName} - {t("customers.pointManagement")}
          </DialogTitle>
          <DialogDescription>
            {t("customers.pointManagementDescription")}
          </DialogDescription>
        </DialogHeader>

        {/* Tab Navigation */}
        <div className="flex border-b">
          <button
            onClick={() => setActiveTab('overview')}
            className={`px-4 py-2 text-sm font-medium border-b-2 transition-colors ${
              activeTab === 'overview'
                ? 'border-green-500 text-green-600'
                : 'border-transparent text-gray-500 hover:text-gray-700'
            }`}
          >
            {t("customers.pointOverview")}
          </button>
          <button
            onClick={() => setActiveTab('update')}
            className={`px-4 py-2 text-sm font-medium border-b-2 transition-colors ${
              activeTab === 'update'
                ? 'border-green-500 text-green-600'
                : 'border-transparent text-gray-500 hover:text-gray-700'
            }`}
          >
            {t("customers.updatePoints")}
          </button>
          <button
            onClick={() => setActiveTab('history')}
            className={`px-4 py-2 text-sm font-medium border-b-2 transition-colors ${
              activeTab === 'history'
                ? 'border-green-500 text-green-600'
                : 'border-transparent text-gray-500 hover:text-gray-700'
            }`}
          >
            {t("customers.pointHistory")}
          </button>
        </div>

        <div className="mt-6">
          {activeTab === 'overview' && (
            <div className="space-y-6">
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center gap-2">
                    <CreditCard className="h-5 w-5" />
                    {t("customers.currentPoints")}
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  {pointsLoading ? (
                    <div className="animate-pulse">
                      <div className="h-8 bg-gray-200 rounded w-32"></div>
                    </div>
                  ) : (
                    <div className="text-3xl font-bold text-green-600">
                      {(pointsData?.points || 0).toLocaleString()} P
                    </div>
                  )}
                </CardContent>
              </Card>

              {/* Recent Transactions Preview */}
              {pointHistory && pointHistory.length > 0 && (
                <Card>
                  <CardHeader>
                    <CardTitle>{t("customers.recentPointHistory")}</CardTitle>
                  </CardHeader>
                  <CardContent>
                    <div className="space-y-3">
                      {pointHistory.slice(0, 5).map((transaction: PointTransaction) => (
                        <div key={transaction.id} className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                          <div className="flex items-center gap-3">
                            {getTransactionIcon(transaction.type)}
                            <div>
                              <div className="font-medium">{transaction.description}</div>
                              <div className="text-sm text-gray-500">
                                {format(new Date(transaction.createdAt), 'yyyy-MM-dd HH:mm')}
                              </div>
                            </div>
                          </div>
                          <div className="text-right">
                            <div className={`font-medium ${getTransactionColor(transaction.type)}`}>
                              {transaction.points > 0 ? '+' : ''}{transaction.points.toLocaleString()} P
                            </div>
                            <div className="text-sm text-gray-500">
                              {t("customers.balance")}: {transaction.newBalance.toLocaleString()} P
                            </div>
                          </div>
                        </div>
                      ))}
                    </div>
                    {pointHistory.length > 5 && (
                      <div className="mt-4 text-center">
                        <Button
                          variant="outline"
                          onClick={() => setActiveTab('history')}
                        >
                          {t("customers.viewAllHistory")}
                        </Button>
                      </div>
                    )}
                  </CardContent>
                </Card>
              )}
            </div>
          )}

          {activeTab === 'update' && (
            <Card>
              <CardHeader>
                <CardTitle>{t("customers.updatePoints")}</CardTitle>
              </CardHeader>
              <CardContent>
                <Form {...form}>
                  <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                    <FormField
                      control={form.control}
                      name="type"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>{t("customers.transactionType")}</FormLabel>
                          <Select onValueChange={field.onChange} value={field.value}>
                            <FormControl>
                              <SelectTrigger>
                                <SelectValue placeholder={t("customers.selectTransactionType")} />
                              </SelectTrigger>
                            </FormControl>
                            <SelectContent>
                              <SelectItem value="earned">{t("customers.earned")}</SelectItem>
                              <SelectItem value="redeemed">{t("customers.redeemed")}</SelectItem>
                              <SelectItem value="adjusted">{t("customers.adjusted")}</SelectItem>
                            </SelectContent>
                          </Select>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={form.control}
                      name="points"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>{t("customers.points")}</FormLabel>
                          <FormControl>
                            <Input
                              type="number"
                              placeholder={t("customers.enterPoints")}
                              {...field}
                              onChange={(e) => field.onChange(parseInt(e.target.value) || 0)}
                            />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={form.control}
                      name="description"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>{t("customers.reason")}</FormLabel>
                          <FormControl>
                            <Textarea
                              placeholder={t("customers.enterReason")}
                              {...field}
                            />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <div className="flex gap-2 pt-4">
                      <Button
                        type="submit"
                        disabled={updatePointsMutation.isPending}
                        className="bg-green-600 hover:bg-green-700"
                      >
                        {updatePointsMutation.isPending ? t("common.processing") : t("customers.processPoints")}
                      </Button>
                      <Button
                        type="button"
                        variant="outline"
                        onClick={() => form.reset()}
                      >
                        {t("common.reset")}
                      </Button>
                    </div>
                  </form>
                </Form>
              </CardContent>
            </Card>
          )}

          {activeTab === 'history' && (
            <Card>
              <CardHeader>
                <CardTitle>{t("customers.pointHistory")}</CardTitle>
              </CardHeader>
              <CardContent>
                {historyLoading ? (
                  <div className="space-y-3">
                    {[...Array(5)].map((_, i) => (
                      <div key={i} className="animate-pulse">
                        <div className="h-16 bg-gray-200 rounded"></div>
                      </div>
                    ))}
                  </div>
                ) : pointHistory && pointHistory.length > 0 ? (
                  <div className="space-y-3">
                    {pointHistory.map((transaction: PointTransaction) => (
                      <div key={transaction.id} className="flex items-center justify-between p-4 border rounded-lg">
                        <div className="flex items-center gap-3">
                          {getTransactionIcon(transaction.type)}
                          <div>
                            <div className="font-medium">{transaction.description}</div>
                            <div className="text-sm text-gray-500">
                              {format(new Date(transaction.createdAt), 'yyyy-MM-dd HH:mm:ss')}
                            </div>
                            <Badge variant="outline" className="mt-1">
                              {getTypeLabel(transaction.type)}
                            </Badge>
                          </div>
                        </div>
                        <div className="text-right">
                          <div className={`font-medium ${getTransactionColor(transaction.type)}`}>
                            {transaction.points > 0 ? '+' : ''}{transaction.points.toLocaleString()} P
                          </div>
                          <div className="text-sm text-gray-500">
                            {t("customers.previousBalance")}: {transaction.previousBalance.toLocaleString()} P
                          </div>
                          <div className="text-sm font-medium">
                            {t("customers.balance")}: {transaction.newBalance.toLocaleString()} P
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                ) : (
                  <div className="text-center py-8 text-gray-500">
                    {t("customers.noPointHistory")}
                  </div>
                )}
              </CardContent>
            </Card>
          )}
        </div>
      </DialogContent>
    </Dialog>
  );
}

