import { useState } from "react";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { useTranslation } from "@/lib/i18n";
import { type Customer } from "@shared/schema";
import { 
  CreditCard, 
  Plus, 
  Minus, 
  History, 
  Search,
  Calculator,
  Gift,
  AlertCircle,
  TrendingUp,
  TrendingDown,
  X,
  Save
} from "lucide-react";

interface PointsManagementModalProps {
  isOpen: boolean;
  onClose: () => void;
}

interface PointTransaction {
  id: number;
  customerId: number;
  points: number;
  type: 'earned' | 'redeemed' | 'adjusted' | 'expired';
  description: string;
  createdAt: string;
}

export function PointsManagementModal({ isOpen, onClose }: PointsManagementModalProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const { t } = useTranslation();

  // State for point operations
  const [selectedCustomer, setSelectedCustomer] = useState<Customer | null>(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [pointsAmount, setPointsAmount] = useState('');
  const [adjustmentReason, setAdjustmentReason] = useState('');
  const [operationType, setOperationType] = useState<'add' | 'subtract' | 'set'>('add');
  const [activeTab, setActiveTab] = useState('management');

  // Fetch customers
  const { data: customers, isLoading: customersLoading } = useQuery<Customer[]>({
    queryKey: ['/api/customers'],
    enabled: isOpen,
  });

  // Fetch point transactions history
  const { data: pointTransactions, isLoading: transactionsLoading } = useQuery<PointTransaction[]>({
    queryKey: ['/api/point-transactions'],
    enabled: isOpen && activeTab === 'history',
  });

  // Point adjustment mutation
  const adjustPointsMutation = useMutation({
    mutationFn: async ({ customerId, points, type, description }: { 
      customerId: number; 
      points: number; 
      type: string; 
      description: string 
    }) => {
      const response = await apiRequest('POST', '/api/customers/adjust-points', {
        customerId,
        points,
        type,
        description
      });
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/customers'] });
      queryClient.invalidateQueries({ queryKey: ['/api/point-transactions'] });
      queryClient.invalidateQueries({ queryKey: ['customer-points', selectedCustomer?.id] });
      toast({
        title: t("customers.customerUpdated"),
        description: t("customers.pointsManagementDesc"),
      });
      setPointsAmount('');
      setAdjustmentReason('');
      setSelectedCustomer(null);
    },
    onError: () => {
      toast({
        title: t("common.error"),
        description: t("customers.customerError"),
        variant: 'destructive',
      });
    },
  });

  // Point payment mutation
  const processPaymentMutation = useMutation({
    mutationFn: async ({ customerId, points }: { customerId: number; points: number }) => {
      const response = await apiRequest('POST', '/api/customers/redeem-points', {
        customerId,
        points
      });
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/customers'] });
      queryClient.invalidateQueries({ queryKey: ['/api/point-transactions'] });
      queryClient.invalidateQueries({ queryKey: ['customer-points', selectedCustomer?.id] });
      toast({
        title: t("common.success"),
        description: t("customers.processPayment"),
      });
      setPointsAmount('');
      setSelectedCustomer(null);
    },
    onError: () => {
      toast({
        title: t("common.error"),
        description: t("customers.customerError"),
        variant: 'destructive',
      });
    },
  });

  const handlePointsAdjustment = () => {
    if (!selectedCustomer || !pointsAmount || !adjustmentReason) {
      toast({
        title: t("common.error"),
        description: t("customers.customerFormDesc"),
        variant: 'destructive',
      });
      return;
    }

    const points = parseInt(pointsAmount);
    const currentPoints = selectedCustomer.points || 0;

    let finalPoints = points;
    let type = 'adjusted';

    if (operationType === 'add') {
      finalPoints = points;
      type = 'earned';
    } else if (operationType === 'subtract') {
      finalPoints = -points;
      type = 'redeemed';
    } else if (operationType === 'set') {
      finalPoints = points - currentPoints;
      type = 'adjusted';
    }

    adjustPointsMutation.mutate({
      customerId: selectedCustomer.id,
      points: finalPoints,
      type,
      description: adjustmentReason
    });
  };

  const handlePointsPayment = () => {
    if (!selectedCustomer || !pointsAmount) {
      toast({
        title: t("common.error"),
        description: t("customers.customerSelection"),
        variant: 'destructive',
      });
      return;
    }

    const points = parseInt(pointsAmount);
    const currentPoints = selectedCustomer.points || 0;

    if (points > currentPoints) {
      toast({
        title: t("customers.insufficientPoints"),
        description: t("customers.insufficientPoints"),
        variant: 'destructive',
      });
      return;
    }

    processPaymentMutation.mutate({
      customerId: selectedCustomer.id,
      points
    });
  };

  const filteredCustomers = customers?.filter(customer => {
    const searchLower = searchTerm.toLowerCase();
    return customer.name?.toLowerCase().includes(searchLower) ||
           customer.customerId?.toLowerCase().includes(searchLower) ||
           customer.phone?.toLowerCase().includes(searchLower);
  }) || [];

  const totalPoints = customers?.reduce((sum, customer) => sum + (customer.points || 0), 0) || 0;
  const avgPoints = customers?.length ? Math.round(totalPoints / customers.length) : 0;

  if (!isOpen) return null;

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-6xl max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <CreditCard className="w-6 h-6 text-blue-600" />
            {t("customers.pointsManagementTitle")}
          </DialogTitle>
          <DialogDescription>
            {t("customers.pointsManagementDesc")}
          </DialogDescription>
        </DialogHeader>

        <Tabs value={activeTab} onValueChange={setActiveTab} className="space-y-6">
          <TabsList className="grid w-full grid-cols-3">
            <TabsTrigger value="management">{t("customers.pointsAdjustment")}</TabsTrigger>
            <TabsTrigger value="payment">{t("customers.pointsPayment")}</TabsTrigger>
            <TabsTrigger value="history">{t("customers.history")}</TabsTrigger>
          </TabsList>

          {/* Points Statistics */}
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <Card>
              <CardContent className="p-4">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-sm font-medium text-gray-600">{t("customers.totalPoints")}</p>
                    <p className="text-2xl font-bold text-blue-600">{totalPoints.toLocaleString()}P</p>
                  </div>
                  <CreditCard className="w-8 h-8 text-blue-600" />
                </div>
              </CardContent>
            </Card>

            <Card>
              <CardContent className="p-4">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-sm font-medium text-gray-600">{t("customers.averagePoints")}</p>
                    <p className="text-2xl font-bold text-green-600">{avgPoints.toLocaleString()}P</p>
                  </div>
                  <Calculator className="w-8 h-8 text-green-600" />
                </div>
              </CardContent>
            </Card>

            <Card>
              <CardContent className="p-4">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-sm font-medium text-gray-600">{t("customers.total")} {t("customers.customers")}</p>
                    <p className="text-2xl font-bold text-purple-600">{customers?.length || 0}{t("customers.customers")}</p>
                  </div>
                  <Gift className="w-8 h-8 text-purple-600" />
                </div>
              </CardContent>
            </Card>
          </div>

          <TabsContent value="management" className="space-y-6">
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Calculator className="w-5 h-5 text-blue-600" />
                  {t("customers.pointsEditTitle")}
                </CardTitle>
                <CardDescription>{t("customers.pointsEditDesc")}</CardDescription>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  {/* Customer Selection */}
                  <div className="space-y-4">
                    <Label>{t("customers.customerSelection")}</Label>
                    <div className="space-y-2">
                      <Input
                        placeholder={t("customers.searchCustomers")}
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                      />
                      <div className="max-h-64 overflow-y-auto border rounded-md">
                        {filteredCustomers.map((customer) => (
                          <div
                            key={customer.id}
                            className={`p-3 cursor-pointer hover:bg-gray-50 border-b ${
                              selectedCustomer?.id === customer.id ? 'bg-blue-50 border-blue-200' : ''
                            }`}
                            onClick={() => setSelectedCustomer(customer)}
                          >
                            <div className="flex justify-between items-center">
                              <div>
                                <p className="font-medium">{customer.name}</p>
                                <p className="text-sm text-gray-500">{customer.customerId}</p>
                              </div>
                              <Badge variant="outline">
                                {(customer.points || 0).toLocaleString()}P
                              </Badge>
                            </div>
                          </div>
                        ))}
                      </div>
                    </div>
                  </div>

                  {/* Point Adjustment */}
                  <div className="space-y-4">
                    <Label>{t("customers.pointsAdjustment")}</Label>
                    {selectedCustomer && (
                      <div className="p-4 bg-blue-50 rounded-lg">
                        <p className="font-medium">{selectedCustomer.name}</p>
                        <p className="text-sm text-gray-600">{t("customers.currentPoints")}: {(selectedCustomer.points || 0).toLocaleString()}P</p>
                      </div>
                    )}

                    <div className="space-y-3">
                      <div>
                        <Label>{t("customers.adjustmentType")}</Label>
                        <Select value={operationType} onValueChange={(value: 'add' | 'subtract' | 'set') => setOperationType(value)}>
                          <SelectTrigger>
                            <SelectValue />
                          </SelectTrigger>
                          <SelectContent>
                            <SelectItem value="add">{t("customers.addPoints")}</SelectItem>
                            <SelectItem value="subtract">{t("customers.subtractPoints")}</SelectItem>
                            <SelectItem value="set">{t("customers.setPoints")}</SelectItem>
                          </SelectContent>
                        </Select>
                      </div>

                      <div>
                        <Label>
                          {operationType === 'add' ? t("customers.pointsToAdd") : 
                           operationType === 'subtract' ? t("customers.pointsToSubtract") : 
                           t("customers.pointsToSet")}
                        </Label>
                        <Input
                          type="number"
                          placeholder={t("customers.pointsToUsePlaceholder")}
                          value={pointsAmount}
                          onChange={(e) => setPointsAmount(e.target.value)}
                        />
                      </div>

                      <div>
                        <Label>{t("customers.adjustmentReason")}</Label>
                        <Textarea
                          placeholder={t("customers.adjustmentReasonPlaceholder")}
                          value={adjustmentReason}
                          onChange={(e) => setAdjustmentReason(e.target.value)}
                        />
                      </div>

                      <Button 
                        onClick={handlePointsAdjustment}
                        className="w-full"
                        disabled={!selectedCustomer || !pointsAmount || !adjustmentReason || adjustPointsMutation.isPending}
                      >
                        {adjustPointsMutation.isPending ? t("customers.processing") : 
                         operationType === 'add' ? <Plus className="w-4 h-4 mr-2" /> : 
                         operationType === 'subtract' ? <Minus className="w-4 h-4 mr-2" /> : 
                         <Calculator className="w-4 h-4 mr-2" />}
                        {operationType === 'add' ? t("customers.addPoints") : 
                         operationType === 'subtract' ? t("customers.subtractPoints") : 
                         t("customers.setPoints")}
                      </Button>
                    </div>
                  </div>
                </div>
              </CardContent>
            </Card>
          </TabsContent>

          <TabsContent value="payment" className="space-y-6">
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <CreditCard className="w-5 h-5 text-green-600" />
                  {t("customers.pointsPaymentTitle")}
                </CardTitle>
                <CardDescription>{t("customers.pointsPaymentDesc")}</CardDescription>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  {/* Customer Selection for Payment */}
                  <div className="space-y-4">
                    <Label>{t("customers.paymentCustomerSelection")}</Label>
                    <div className="space-y-2">
                      <Input
                        placeholder={t("customers.searchCustomers")}
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                      />
                      <div className="max-h-64 overflow-y-auto border rounded-md">
                        {filteredCustomers.map((customer) => (
                          <div
                            key={customer.id}
                            className={`p-3 cursor-pointer hover:bg-gray-50 border-b ${
                              selectedCustomer?.id === customer.id ? 'bg-green-50 border-green-200' : ''
                            }`}
                            onClick={() => setSelectedCustomer(customer)}
                          >
                            <div className="flex justify-between items-center">
                              <div>
                                <p className="font-medium">{customer.name}</p>
                                <p className="text-sm text-gray-500">{customer.customerId}</p>
                              </div>
                              <Badge variant="outline" className={
                                (customer.points || 0) >= 1000 ? 'bg-green-100 text-green-800' : 
                                (customer.points || 0) >= 500 ? 'bg-yellow-100 text-yellow-800' : 
                                'bg-gray-100 text-gray-800'
                              }>
                                {(customer.points || 0).toLocaleString()}P
                              </Badge>
                            </div>
                          </div>
                        ))}
                      </div>
                    </div>
                  </div>

                  {/* Payment Processing */}
                  <div className="space-y-4">
                    <Label>{t("customers.pointsPayment")}</Label>
                    {selectedCustomer && (
                      <div className="p-4 bg-green-50 rounded-lg">
                        <p className="font-medium">{selectedCustomer.name}</p>
                        <p className="text-sm text-gray-600">{t("customers.availablePoints")}: {(selectedCustomer.points || 0).toLocaleString()}P</p>
                      </div>
                    )}

                    <div className="space-y-3">
                      <div>
                        <Label>{t("customers.pointsToUse")}</Label>
                        <Input
                          type="number"
                          placeholder={t("customers.pointsToUsePlaceholder")}
                          value={pointsAmount}
                          onChange={(e) => setPointsAmount(e.target.value)}
                        />
                        {selectedCustomer && pointsAmount && parseInt(pointsAmount) > (selectedCustomer.points || 0) && (
                          <p className="text-sm text-red-600 flex items-center gap-1 mt-1">
                            <AlertCircle className="w-4 h-4" />
                            {t("customers.insufficientPoints")}
                          </p>
                        )}
                      </div>

                      {pointsAmount && selectedCustomer && (
                        <div className="p-3 bg-gray-50 rounded-lg">
                          <div className="flex justify-between text-sm">
                            <span>{t("customers.pointsAfterPayment")}:</span>
                            <span className="font-medium">
                              {Math.max(0, (selectedCustomer.points || 0) - parseInt(pointsAmount || '0')).toLocaleString()}P
                            </span>
                          </div>
                        </div>
                      )}

                      <Button 
                        onClick={handlePointsPayment}
                        className="w-full bg-green-600 hover:bg-green-700"
                        disabled={!selectedCustomer || !pointsAmount || parseInt(pointsAmount || '0') > (selectedCustomer?.points || 0) || processPaymentMutation.isPending}
                      >
                        {processPaymentMutation.isPending ? t("customers.processing") : 
                         <>
                           <CreditCard className="w-4 h-4 mr-2" />
                           {t("customers.processPayment")}
                         </>}
                      </Button>
                    </div>
                  </div>
                </div>
              </CardContent>
            </Card>
          </TabsContent>

          <TabsContent value="history" className="space-y-6">
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <History className="w-5 h-5 text-purple-600" />
                  {t("customers.historyTitle")}
                </CardTitle>
                <CardDescription>{t("customers.historyDesc")}</CardDescription>
              </CardHeader>
              <CardContent>
                {transactionsLoading ? (
                  <div className="text-center py-8">
                    <p className="text-gray-500">{t("customers.loadingHistory")}</p>
                  </div>
                ) : !pointTransactions || pointTransactions.length === 0 ? (
                  <div className="text-center py-8">
                    <History className="w-12 h-12 mx-auto text-gray-400 mb-4" />
                    <p className="text-gray-500">{t("customers.noHistory")}</p>
                  </div>
                ) : (
                  <div className="space-y-3">
                    {pointTransactions.map((transaction) => {
                      const customer = customers?.find(c => c.id === transaction.customerId);
                      return (
                        <div key={transaction.id} className="flex items-center justify-between p-4 border rounded-lg">
                          <div className="flex items-center gap-3">
                            <div className={`w-10 h-10 rounded-full flex items-center justify-center ${
                              transaction.type === 'earned' ? 'bg-green-100' :
                              transaction.type === 'redeemed' ? 'bg-red-100' :
                              transaction.type === 'adjusted' ? 'bg-blue-100' :
                              'bg-gray-100'
                            }`}>
                              {transaction.type === 'earned' ? <TrendingUp className="w-5 h-5 text-green-600" /> :
                               transaction.type === 'redeemed' ? <TrendingDown className="w-5 h-5 text-red-600" /> :
                               transaction.type === 'adjusted' ? <Calculator className="w-5 h-5 text-blue-600" /> :
                               <AlertCircle className="w-5 h-5 text-gray-600" />}
                            </div>
                            <div>
                              <p className="font-medium">{customer?.name || t("customers.unknown")}</p>
                              <p className="text-sm text-gray-600">{transaction.description}</p>
                              <p className="text-xs text-gray-400">
                                {new Date(transaction.createdAt).toLocaleDateString('ko-KR', {
                                  year: 'numeric',
                                  month: 'short',
                                  day: 'numeric',
                                  hour: '2-digit',
                                  minute: '2-digit'
                                })}
                              </p>
                            </div>
                          </div>
                          <div className="text-right">
                            <p className={`font-medium ${
                              transaction.points > 0 ? 'text-green-600' : 'text-red-600'
                            }`}>
                              {transaction.points > 0 ? '+' : ''}{transaction.points.toLocaleString()}P
                            </p>
                            <Badge variant="outline" className="mt-1">
                              {transaction.type === 'earned' ? t("customers.earned") :
                               transaction.type === 'redeemed' ? t("customers.redeemed") :
                               transaction.type === 'adjusted' ? t("customers.adjusted") :
                               t("customers.expired")}
                            </Badge>
                          </div>
                        </div>
                      );
                    })}
                  </div>
                )}
              </CardContent>
            </Card>
          </TabsContent>
        </Tabs>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/employees/employee.content:
import React from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import {
  insertEmployeeSchema,
  type Employee,
  type InsertEmployee,
} from "@shared/schema";
import { useTranslation } from "@/lib/i18n";
import { z } from "zod";

interface EmployeeFormModalProps {
  isOpen: boolean;
  onClose: () => void;
  mode: "create" | "edit";
  employee?: Employee;
}

export function EmployeeFormModal({
  isOpen,
  onClose,
  mode,
  employee,
}: EmployeeFormModalProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const { t } = useTranslation();

  // Create translated validation schema
  const translatedEmployeeSchema = z.object({
    employeeId: z.string().min(1, t("employees.employeeIdRequired")),
    name: z.string().min(1, t("employees.nameRequired")),
    email: z.string().email(t("employees.emailInvalid")).optional().or(z.literal("")),
    phone: z.string().nullable(),
    role: z.enum(["admin", "manager", "cashier", "kitchen", "server"]),
    isActive: z.boolean(),
    hireDate: z.date(),
  });

  // Generate employee ID for new employees
  const generateEmployeeId = async () => {
    try {
      const response = await apiRequest("GET", "/api/employees/next-id");
      const data = await response.json();
      return data.nextId;
    } catch (error) {
      console.error("Error generating employee ID:", error);
      // Fallback to EMP-001 if API fails
      return "EMP-001";
    }
  };

  const form = useForm<InsertEmployee>({
    resolver: zodResolver(translatedEmployeeSchema),
    defaultValues: {
      employeeId: "",
      name: "",
      email: "",
      phone: null,
      role: "cashier",
      isActive: true,
      hireDate: new Date(),
    },
  });

  // Set employee ID for new employees or populate form for edit
  React.useEffect(() => {
    if (mode === "create" && !employee?.employeeId) {
      generateEmployeeId().then((nextId) => {
        form.reset({
          employeeId: nextId,
          name: "",
          email: "",
          phone: null,
          role: "cashier",
          isActive: true,
          hireDate: new Date(),
        });
      });
    } else if (mode === "edit" && employee) {
      form.reset({
        employeeId: employee.employeeId,
        name: employee.name,
        email: employee.email || "",
        phone: employee.phone || null,
        role: employee.role,
        isActive: employee.isActive ?? true,
        hireDate: employee.hireDate ? new Date(employee.hireDate) : new Date(),
      });
    }
  }, [mode, employee, form]);

  const createMutation = useMutation({
    mutationFn: async (data: InsertEmployee) => {
      const response = await apiRequest("POST", "/api/employees", data);
      if (!response.ok) {
        const errorData = await response.json();
        throw errorData;
      }
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/employees"] });
      toast({
        title: t("common.success"),
        description: t("employees.addEmployeeSuccess"),
      });
      onClose();
      generateEmployeeId().then((nextId) => {
        form.reset({
          employeeId: nextId,
          name: "",
          email: "",
          phone: null,
          role: "cashier",
          isActive: true,
          hireDate: new Date(),
        });
      });
    },
    onError: (error: any) => {
      let errorMessage = t("employees.addEmployeeError");
      // Checking for the error in the expected format
      if (error instanceof Error && error.message) {
        const errorData = JSON.parse(
          error.message.slice(error.message.indexOf("{")),
        ); // Extract JSON part of the message
        if (errorData.code === "DUPLICATE_EMAIL") {
          errorMessage = t("employees.emailAlreadyExists");
          // Set error on the email field
          form.setError("email", {
            type: "manual",
            message: t("employees.emailAlreadyInSystem"),
          });
        }
      }
      toast({
        title: t("common.error"),
        description: errorMessage,
        variant: "destructive",
      });
    },
  });

  const updateMutation = useMutation({
    mutationFn: async (data: InsertEmployee) => {
      const response = await apiRequest(
        "PUT",
        `/api/employees/${employee?.id}`,
        data,
      );
      if (!response.ok) {
        const errorData = await response.json();
        throw errorData;
      }
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/employees"] });
      toast({
        title: t("common.success"),
        description: t("employees.updateEmployeeSuccess"),
      });
      onClose();
    },
    onError: (error: any) => {
      let errorMessage = t("employees.updateEmployeeError");

      // Parse error similar to createMutation
      if (error && typeof error === 'object' && error.code === "DUPLICATE_EMAIL") {
        errorMessage = t("employees.emailAlreadyExists");
        form.setError("email", {
          type: "manual",
          message: t("employees.emailAlreadyInSystem"),
        });
      }

      toast({
        title: t("common.error"),
        description: errorMessage,
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: InsertEmployee) => {
    if (mode === "create") {
      createMutation.mutate(data);
    } else {
      updateMutation.mutate(data);
    }
  };

  const isLoading = createMutation.isPending || updateMutation.isPending;

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="sm:max-w-[500px]">
        <DialogHeader>
          <DialogTitle>
            {mode === "create"
              ? t("employees.addEmployee")
              : t("employees.editEmployee")}
          </DialogTitle>
          <DialogDescription>
            {mode === "create"
              ? t("employees.employeeFormDesc")
              : t("employees.employeeFormDesc")}
          </DialogDescription>
        </DialogHeader>

        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
            <FormField
              control={form.control}
              name="employeeId"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>{t("employees.employeeId")}</FormLabel>
                  <FormControl>
                    <Input
                      placeholder="EMP001"
                      {...field}
                      readOnly={true}
                      className="bg-gray-100"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="name"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>
                    {t("employees.name")}{" "}
                    <span className="text-red-500">*</span>
                  </FormLabel>
                  <FormControl>
                    <Input
                      placeholder={t("employees.namePlaceholder")}
                      {...field}
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="email"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>{t("employees.email")}</FormLabel>
                  <FormControl>
                    <Input
                      type="email"
                      placeholder="hong@company.com"
                      {...field}
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="phone"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>{t("employees.phone")}</FormLabel>
                  <FormControl>
                    <Input
                      placeholder="010-1234-5678"
                      value={field.value || ""}
                      onChange={field.onChange}
                      onBlur={field.onBlur}
                      name={field.name}
                      ref={field.ref}
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="role"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>{t("employees.role")}</FormLabel>
                  <Select
                    onValueChange={field.onChange}
                    defaultValue={field.value}
                  >
                    <FormControl>
                      <SelectTrigger>
                        <SelectValue placeholder={t("employees.selectRole")} />
                      </SelectTrigger>
                    </FormControl>
                    <SelectContent>
                      <SelectItem value="cashier">
                        {t("employees.roles.cashier")}
                      </SelectItem>
                      <SelectItem value="manager">
                        {t("employees.roles.manager")}
                      </SelectItem>
                      <SelectItem value="admin">
                        {t("employees.roles.admin")}
                      </SelectItem>
                    </SelectContent>
                  </Select>
                  <FormMessage />
                </FormItem>
              )}
            />

            <div className="flex justify-end gap-2 pt-4">
              <Button
                type="button"
                variant="outline"
                onClick={onClose}
                disabled={isLoading}
              >
                {t("common.cancel")}
              </Button>
              <Button type="submit" disabled={isLoading}>
                {isLoading
                  ? t("employees.saving")
                  : mode === "create"
                    ? t("employees.add")
                    : t("employees.save")}
              </Button>
            </div>
          </form>
        </Form>
      </DialogContent>
    </Dialog>
  );
}

import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Edit, Trash2, User, Mail, Phone, Calendar } from "lucide-react";
import { EmployeeFormModal } from "./employee-form-modal";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { useTranslation } from "@/lib/i18n";
import type { Employee } from "@shared/schema";

export function EmployeeList() {
  const [selectedEmployee, setSelectedEmployee] = useState<Employee | null>(null);
  const [showEditModal, setShowEditModal] = useState(false);
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const { t } = useTranslation();

  const { data: employeesData, isLoading } = useQuery<Employee[]>({
    queryKey: ['/api/employees'],
  });

  // Sort employees by ID descending (newest first)
  const employees = employeesData?.sort((a, b) => b.id - a.id);

  const deleteMutation = useMutation({
    mutationFn: async (id: number) => {
      const response = await apiRequest('DELETE', `/api/employees/${id}`);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/employees'] });
      toast({
        title: t('common.success'),
        description: t('employees.deleteSuccess'),
      });
    },
    onError: () => {
      toast({
        title: t('common.error'),
        description: t('employees.deleteError'),
        variant: "destructive",
      });
    },
  });

  const handleEdit = (employee: Employee) => {
    setSelectedEmployee(employee);
    setShowEditModal(true);
  };

  const handleDelete = (employee: Employee) => {
    if (confirm(t('employees.confirmDelete').replace('{{name}}', employee.name))) {
      deleteMutation.mutate(employee.id);
    }
  };

  const getRoleBadge = (role: string) => {
    const roleConfig = {
      admin: { variant: "destructive" as const },
      manager: { variant: "default" as const },
      cashier: { variant: "secondary" as const },
    };

    const config = roleConfig[role as keyof typeof roleConfig] || { variant: "outline" as const };
    const label = t(`employees.roles.${role}` as any) || role;
    return <Badge variant={config.variant}>{label}</Badge>;
  };

  const formatDate = (date: string | Date) => {
    return new Date(date).toLocaleDateString('ko-KR');
  };

  if (isLoading) {
    return (
      <Card>
        <CardContent className="p-6">
          <div className="flex justify-center">
            <div className="text-gray-500">{t('common.loading')}</div>
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <>
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <User className="w-5 h-5" />
            {t('employees.employeeList')}
          </CardTitle>
          <CardDescription>
            {t('employees.currentEmployeeCount').replace('{{count}}', String(employees?.length || 0))}
          </CardDescription>
        </CardHeader>
        <CardContent>
          {!employees || employees.length === 0 ? (
            <div className="text-center py-8">
              <User className="w-12 h-12 mx-auto text-gray-400 mb-4" />
              <p className="text-gray-500">{t('employees.noEmployeesFound')}</p>
              <p className="text-sm text-gray-400 mt-2">{t('employees.addEmployeeHint')}</p>
            </div>
          ) : (
            <div className="overflow-x-auto">
              <Table className="min-w-[1200px]">
                <TableHeader>
                  <TableRow>
                    <TableHead className="min-w-[120px]">{t('employees.employeeId')}</TableHead>
                    <TableHead className="min-w-[150px]">{t('employees.name')}</TableHead>
                    <TableHead className="min-w-[200px]">{t('employees.email')}</TableHead>
                    <TableHead className="min-w-[150px]">{t('employees.phone')}</TableHead>
                    <TableHead className="min-w-[160px]">{t('employees.role')}</TableHead>
                    <TableHead className="min-w-[130px]">{t('employees.hireDate')}</TableHead>
                    <TableHead className="min-w-[140px]">{t('employees.status')}</TableHead>
                    <TableHead className="min-w-[150px]">{t('employees.employeeManagement')}</TableHead>
                  </TableRow>
                </TableHeader>
              <TableBody>
                {employees.map((employee: Employee) => (
                  <TableRow key={employee.id}>
                    <TableCell className="font-medium">{employee.employeeId}</TableCell>
                    <TableCell>
                      <div className="flex items-center gap-2">
                        <User className="w-4 h-4 text-gray-400" />
                        {employee.name}
                      </div>
                    </TableCell>
                    <TableCell>
                      <div className="flex items-center gap-2">
                        <Mail className="w-4 h-4 text-gray-400" />
                        {employee.email}
                      </div>
                    </TableCell>
                    <TableCell>
                      {employee.phone ? (
                        <div className="flex items-center gap-2">
                          <Phone className="w-4 h-4 text-gray-400" />
                          {employee.phone}
                        </div>
                      ) : (
                        <span className="text-gray-400">-</span>
                      )}
                    </TableCell>
                    <TableCell>{getRoleBadge(employee.role)}</TableCell>
                    <TableCell>
                      <div className="flex items-center gap-2">
                        <Calendar className="w-4 h-4 text-gray-400" />
                        {formatDate(employee.hireDate)}
                      </div>
                    </TableCell>
                    <TableCell>
                      <Badge variant={employee.isActive ? "default" : "secondary"}>
                        {employee.isActive ? t('employees.active') : t('employees.inactive')}
                      </Badge>
                    </TableCell>
                    <TableCell>
                      <div className="flex gap-2">
                        <Button 
                          variant="outline" 
                          size="sm"
                          onClick={() => handleEdit(employee)}
                        >
                          <Edit className="w-4 h-4" />
                        </Button>
                        <Button 
                          variant="outline" 
                          size="sm"
                          onClick={() => handleDelete(employee)}
                          disabled={deleteMutation.isPending}
                        >
                          <Trash2 className="w-4 h-4" />
                        </Button>
                      </div>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
            </div>
          )}
        </CardContent>
      </Card>

      {/* Edit Employee Modal */}
      {selectedEmployee && (
        <EmployeeFormModal
          isOpen={showEditModal}
          onClose={() => {
            setShowEditModal(false);
            setSelectedEmployee(null);
          }}
          mode="edit"
          employee={selectedEmployee}
        />
      )}
    </>
  );
}

client/src/components/membership/members.content:
import { useState } from "react";
import * as React from "react";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { useTranslation } from "@/lib/i18n";
import { type Customer } from "@shared/schema";
import { 
  Crown, 
  Medal, 
  Award, 
  Users, 
  TrendingUp, 
  Gift,
  Star,
  Search,
  Save,
  X
} from "lucide-react";

interface MembershipModalProps {
  isOpen: boolean;
  onClose: () => void;
}

export function MembershipModal({ isOpen, onClose }: MembershipModalProps) {
  const { toast } = useToast();
  const { t } = useTranslation();
  const queryClient = useQueryClient();
  const [selectedTier, setSelectedTier] = useState<string>('');
  const [searchTerm, setSearchTerm] = useState('');
  const [isEditingThresholds, setIsEditingThresholds] = useState(false);
  const [thresholds, setThresholds] = useState({
    GOLD: 300000,
    VIP: 1000000
  });

  // Fetch customers
  const { data: customers, isLoading } = useQuery<Customer[]>({
    queryKey: ['/api/customers'],
    enabled: isOpen,
  });

  // Fetch membership thresholds
  const { data: fetchedThresholds } = useQuery<{ GOLD: number; VIP: number }>({
    queryKey: ['/api/membership-thresholds'],
    enabled: isOpen,
  });

  // Update thresholds when data is fetched
  React.useEffect(() => {
    if (fetchedThresholds) {
      setThresholds(fetchedThresholds);
    }
  }, [fetchedThresholds]);

  const membershipTiers = [
    {
      level: 'SILVER',
      name: t("customers.silver"),
      icon: Medal,
      color: 'bg-gray-100 text-gray-800 border-gray-200',
      benefits: [t("customers.basicPointsEarning"), t("customers.birthdayDiscount5")],
      minSpent: 0,
      description: t("customers.basicLevelDesc")
    },
    {
      level: 'GOLD', 
      name: t("customers.gold"),
      icon: Award,
      color: 'bg-yellow-100 text-yellow-800 border-yellow-200',
      benefits: [t("customers.pointsEarning15"), t("customers.birthdayDiscount10"), t("customers.freeDrinkMonthly")],
      minSpent: thresholds.GOLD,
      description: t("customers.premiumLevelDesc")
    },
    {
      level: 'VIP',
      name: t("customers.vip"),
      icon: Crown,
      color: 'bg-purple-100 text-purple-800 border-purple-200',
      benefits: [t("customers.pointsEarning2x"), t("customers.birthdayDiscount20"), t("customers.freeDrink2Monthly"), t("customers.vipRoomAccess")],
      minSpent: thresholds.VIP,
      description: t("customers.vipLevelDesc")
    }
  ];

  // Update customer membership
  const updateMembershipMutation = useMutation({
    mutationFn: async ({ customerId, membershipLevel }: { customerId: number; membershipLevel: string }) => {
      const response = await fetch(`/api/customers/${customerId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ membershipLevel }),
      });
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/customers'] });
      toast({
        title: t("common.success"),
        description: t("customers.customerUpdated"),
      });
    },
    onError: () => {
      toast({
        title: t("common.error"),
        description: t("customers.customerError"),
        variant: 'destructive',
      });
    },
  });

  // Update membership thresholds
  const updateThresholdsMutation = useMutation({
    mutationFn: async (newThresholds: { GOLD: number; VIP: number }) => {
      const response = await fetch('/api/membership-thresholds', {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newThresholds),
      });
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/membership-thresholds'] });
      queryClient.invalidateQueries({ queryKey: ['/api/customers'] });
      toast({
        title: t("common.success"),
        description: "ƒê√£ c·∫≠p nh·∫≠t m·ª©c chi ti√™u n√¢ng h·∫°ng",
      });
    },
    onError: () => {
      toast({
        title: t("common.error"),
        description: "Kh√¥ng th·ªÉ c·∫≠p nh·∫≠t m·ª©c chi ti√™u",
        variant: 'destructive',
      });
    },
  });

  const handleUpdateMembership = (customerId: number, newLevel: string) => {
    updateMembershipMutation.mutate({ customerId, membershipLevel: newLevel });
  };

  const autoUpgradeBasedOnSpending = (customerId: number, totalSpent: number) => {
    let newLevel = 'SILVER';
    if (totalSpent >= thresholds.VIP) newLevel = 'VIP';
    else if (totalSpent >= thresholds.GOLD) newLevel = 'GOLD';

    handleUpdateMembership(customerId, newLevel);
  };

  const saveThresholds = () => {
    updateThresholdsMutation.mutate(thresholds);
    setIsEditingThresholds(false);
  };

  const autoUpgradeAllCustomers = () => {
    customers?.forEach(customer => {
      const totalSpent = parseFloat(customer.totalSpent || '0');
      const currentLevel = customer.membershipLevel || 'SILVER';
      let suggestedLevel = 'SILVER';

      if (totalSpent >= thresholds.VIP) suggestedLevel = 'VIP';
      else if (totalSpent >= thresholds.GOLD) suggestedLevel = 'GOLD';

      if (currentLevel !== suggestedLevel) {
        autoUpgradeBasedOnSpending(customer.id, totalSpent);
      }
    });
  };

  const filteredCustomers = customers?.filter(customer => {
    const matchesSearch = customer.name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         customer.customerId?.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesTier = !selectedTier || selectedTier === 'all' || customer.membershipLevel?.toLowerCase().includes(selectedTier.toLowerCase());
    return matchesSearch && matchesTier;
  }) || [];

  const membershipStats = customers?.reduce((acc, customer) => {
    const level = customer.membershipLevel || 'SILVER';
    acc[level] = (acc[level] || 0) + 1;
    return acc;
  }, {} as Record<string, number>) || {};

  if (!isOpen) return null;

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-6xl max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <Crown className="w-6 h-6 text-purple-600" />
            {t("customers.membershipManagementTitle")}
          </DialogTitle>
          <DialogDescription>
            {t("customers.membershipManagementDesc")}
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-6">
          {/* Membership Tiers Overview */}
          <div>
            <h3 className="text-lg font-semibold mb-4 flex items-center gap-2">
              <Star className="w-5 h-5 text-yellow-500" />
              {t("customers.membershipTierGuide")}
            </h3>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              {membershipTiers.map((tier) => {
                const IconComponent = tier.icon;
                const count = membershipStats[tier.level] || 0;

                return (
                  <Card key={tier.level} className={`border-2 ${tier.color.includes('gray') ? 'border-gray-200' : tier.color.includes('yellow') ? 'border-yellow-200' : 'border-purple-200'}`}>
                    <CardHeader className="pb-3">
                      <CardTitle className="flex items-center gap-2 text-lg">
                        <IconComponent className="w-5 h-5" />
                        {tier.name}
                        <Badge variant="outline" className="ml-auto">
                          {count}Î™Ö
                        </Badge>
                      </CardTitle>
                      <CardDescription>{tier.description}</CardDescription>
                    </CardHeader>
                    <CardContent>
                      <div className="space-y-2">
                        <div className="text-sm font-medium flex items-center gap-2">
                          {t("customers.minPurchaseAmount")}: 
                          {isEditingThresholds && tier.level !== 'SILVER' ? (
                            <Input
                              type="number"
                              value={thresholds[tier.level as keyof typeof thresholds]}
                              onChange={(e) => setThresholds(prev => ({
                                ...prev,
                                [tier.level]: parseInt(e.target.value) || 0
                              }))}
                              className="w-32 h-6 text-xs"
                            />
                          ) : (
                            <span>‚Ç©{tier.minSpent.toLocaleString()}</span>
                          )}
                        </div>
                        <div className="space-y-1">
                          <div className="text-sm font-medium">{t("customers.membershipBenefits")}:</div>
                          {tier.benefits.map((benefit, index) => (
                            <div key={index} className="text-xs text-gray-600 flex items-center gap-1">
                              <Gift className="w-3 h-3" />
                              {benefit}
                            </div>
                          ))}
                        </div>
                      </div>
                    </CardContent>
                  </Card>
                );
              })}
            </div>
          </div>

          {/* Customer Management */}
          <div>
            <div className="flex items-center justify-between mb-4">
              <h3 className="text-lg font-semibold flex items-center gap-2">
                <Users className="w-5 h-5 text-blue-500" />
                {t("customers.customerMembershipManagement")}
              </h3>
              <div className="flex gap-2">
                {isEditingThresholds ? (
                  <>
                    <Button
                      onClick={saveThresholds}
                      size="sm"
                      className="bg-green-600 hover:bg-green-700"
                    >
                      <Save className="w-4 h-4 mr-2" />
                      L∆∞u m·ª©c chi ti√™u
                    </Button>
                    <Button
                      onClick={() => setIsEditingThresholds(false)}
                      variant="outline"
                      size="sm"
                    >
                      <X className="w-4 h-4 mr-2" />
                      H·ªßy
                    </Button>
                  </>
                ) : (
                  <Button
                    onClick={() => setIsEditingThresholds(true)}
                    variant="outline"
                    size="sm"
                  >
                    <Save className="w-4 h-4 mr-2" />
                    S·ª≠a m·ª©c chi ti√™u
                  </Button>
                )}
                <Button
                  onClick={autoUpgradeAllCustomers}
                  variant="outline"
                  size="sm"
                >
                  <TrendingUp className="w-4 h-4 mr-2" />
                  {t("customers.autoUpgrade")}
                </Button>
              </div>
            </div>

            {/* Filters */}
            <div className="flex gap-4 mb-4">
              <div className="flex-1">
                <Input
                  placeholder={t("customers.searchPlaceholder")}
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="max-w-md"
                />
              </div>
              <Select value={selectedTier} onValueChange={setSelectedTier}>
                <SelectTrigger className="w-48">
                  <SelectValue placeholder={t("customers.filterByTier")} />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">{t("customers.allTiers")}</SelectItem>
                  <SelectItem value="silver">{t("customers.silver")}</SelectItem>
                  <SelectItem value="gold">{t("customers.gold")}</SelectItem>
                  <SelectItem value="vip">{t("customers.vip")}</SelectItem>
                </SelectContent>
              </Select>
            </div>

            {/* Customer List */}
            {isLoading ? (
              <div className="text-center py-8">
                <p className="text-gray-500">{t("common.loading")}</p>
              </div>
            ) : (
              <div className="border rounded-lg">
                <div className="grid grid-cols-7 gap-4 p-4 font-medium text-sm text-gray-600 bg-gray-50 border-b">
                  <div>{t("customers.customerId")}</div>
                  <div>{t("customers.name")}</div>
                  <div>{t("customers.membershipLevel")}</div>
                  <div>{t("customers.totalSpent")}</div>
                  <div>{t("customers.visitCount")}</div>
                  <div>{t("customers.points")}</div>
                  <div className="text-center">{t("common.actions")}</div>
                </div>
                <div className="divide-y max-h-96 overflow-y-auto">
                  {filteredCustomers.map((customer) => {
                    const currentTier = membershipTiers.find(t => t.level === (customer.membershipLevel || 'SILVER'));
                    const totalSpent = parseFloat(customer.totalSpent || '0');
                    const suggestedTier = totalSpent >= thresholds.VIP ? 'VIP' : totalSpent >= thresholds.GOLD ? 'GOLD' : 'SILVER';
                    const needsUpgrade = suggestedTier !== (customer.membershipLevel || 'SILVER');

                    return (
                      <div key={customer.id} className="grid grid-cols-7 gap-4 p-4 items-center">
                        <div className="font-mono text-sm">{customer.customerId || ''}</div>
                        <div className="font-medium">{customer.name}</div>
                        <div>
                          <Badge className={currentTier?.color || 'bg-gray-100 text-gray-800'}>
                            {currentTier?.name || customer.membershipLevel}
                          </Badge>
                          {needsUpgrade && (
                            <Badge variant="outline" className="ml-1 text-orange-600">
                              {t("customers.upgradeAvailable")}
                            </Badge>
                          )}
                        </div>
                        <div className="text-sm">{totalSpent.toLocaleString()} ‚Ç´</div>
                        <div className="text-sm">{customer.visitCount || 0}Ìöå</div>
                        <div className="text-sm">{customer.points || 0}P</div>
                        <div className="flex items-center justify-center gap-2">
                          <Select
                            value={customer.membershipLevel || 'SILVER'}
                            onValueChange={(newLevel) => handleUpdateMembership(customer.id, newLevel)}
                          >
                            <SelectTrigger className="w-20 h-8">
                              <SelectValue />
                            </SelectTrigger>
                            <SelectContent>
                              <SelectItem value="SILVER">{t("customers.silver")}</SelectItem>
                              <SelectItem value="GOLD">{t("customers.gold")}</SelectItem>
                              <SelectItem value="VIP">{t("customers.vip")}</SelectItem>
                            </SelectContent>
                          </Select>
                          {needsUpgrade && (
                            <Button
                              size="sm"
                              variant="outline"
                              onClick={() => autoUpgradeBasedOnSpending(customer.id, totalSpent)}
                              className="text-orange-600 hover:text-orange-700"
                            >
                              <TrendingUp className="w-3 h-3" />
                            </Button>
                          )}
                        </div>
                      </div>
                    );
                  })}
                </div>
              </div>
            )}
          </div>
        </div>

        <div className="flex justify-end gap-2 pt-4 border-t">
          <Button variant="outline" onClick={onClose}>
            <X className="w-4 h-4 mr-2" />
            {t("customers.close")}
          </Button>
        </div>
      </DialogContent>
    </Dialog>
  );
}

