                  </Button>
                </div>

                <div className="flex items-center space-x-2">
                  <Input
                    placeholder="T√¨m ki·∫øm theo t√™n ho·∫∑c m√£ SKU..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="max-w-md"
                  />
                  {searchTerm && (
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => setSearchTerm("")}
