  customerTaxCode?: string;
}

export function ReceiptModal({
  isOpen,
  onClose,
  receipt,
  onConfirm,
  isPreview = false,
  cartItems = [],
  total,
  isEInvoice = false,
  customerName,
  customerTaxCode,
}: ReceiptModalProps) {
  const [showEInvoiceModal, setShowEInvoiceModal] = useState(false);
