      employeeProfit[employeeName].revenue += amount;
      employeeProfit[employeeName].netRevenue += amount;
      employeeProfit[employeeName].totalCost += estimatedCost;
      employeeProfit[employeeName].grossProfit += amount - estimatedCost;
    });

